INSERT INTO `web_product` VALUES ('410', null, 'Chương trình hè Trung học LEARN MY WAY 2016', '12800000', '13800000', '12800000', '1', 'Đăng ký và nộp phí trước 30/05/2016 để nhận được ưu đãi giảm 1.000.000 đ. Mỗi học sinh tham gia khóa hè còn được nhận ngay nhiều quà tặng cực dễ thương: áo phông, mũ lưỡi chai và balo rút dây để dễ dàng thuận lợi hòa mình vào hè năng động.', '2', '<p><strong>5 tuần học B&Aacute;N TR&Uacute;&nbsp;với 125 giờ TRẢI NGHIỆM</strong></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0)\">Đến với Chương tr&igrave;nh h&egrave; LEARN MY WAY 2016 d&agrave;nh cho học sinh Trung học từ 11 đến 16 tuổi của Wellspring, c&aacute;c bạn học sinh sẽ được học h&egrave; theo c&aacute;ch của m&igrave;nh.</span></p>\r\n', '<h2>Vui học H&egrave; c&ugrave;ng những trải nghiệm của đam m&ecirc;</h2>\r\n\r\n<h2><strong><span style=\"font-family:arial,helvetica,sans-serif; font-size:12pt\">Ngoảnh đi ngoảnh lại đ&atilde; sắp hết năm học. C&aacute;c bạn học sinh sắp ch&agrave;o đ&oacute;n một m&ugrave;a h&egrave; với những chuyến nghỉ m&aacute;t th&uacute; vị c&ugrave;ng gia đ&igrave;nh cũng như trải nghiệm c&aacute;c hoạt động vui H&egrave; bổ &iacute;ch.</span></strong></h2>\r\n\r\n<div class=\"fon34 mt3 mr2 fon43 detail-content\" id=\"divNewsContent\" style=\"box-sizing: border-box; border: 0px; font-family: Arial, Helvetica, sans-serif; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; direction: ltr; color: rgb(0, 0, 0); font-size: 14px; line-height: 22.75px;\">\r\n<div class=\"entry-content clearfix\" style=\"box-sizing: border-box; border: 0px; font-family: Arial, Helvetica, sans-serif; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; outline: 0px; padding: 0px; vertical-align: baseline; direction: ltr; color: rgb(0, 0, 0); font-size: 14px; line-height: 22.75px; margin-top: 2rem !important;\">\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">H&egrave; đến thường mang lại kh&ocirc;ng &iacute;t lo lắng với c&aacute;c bậc phụ huynh v&igrave; cha mẹ vẫn phải đi l&agrave;m, con nghỉ h&egrave; kh&ocirc;ng đi học th&igrave; sẽ đi đ&acirc;u, l&agrave;m g&igrave; để vừa được nghỉ ngơi sau cả 1 năm học căng thẳng, kh&ocirc;ng l&atilde;ng qu&ecirc;n kiến thức m&agrave; vẫn được trải nghiệm, tận hưởng những niềm vui tuổi học tr&ograve;? Đ&oacute; l&agrave; chưa kể mong muốn c&aacute;c con c&oacute; thời gian v&agrave; năng lượng để kh&aacute;m ph&aacute;, trải nghiệm th&ecirc;m những điều mới lạ kh&aacute;c ngo&agrave;i s&aacute;ch vở?</span></p>\r\n\r\n<div class=\"fon34 mt3 mr2 fon43 detail-content\" id=\"divNewsContent\" style=\"box-sizing: border-box; border: 0px; font-family: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; direction: ltr;\">\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">Trong những năm gần đ&acirc;y, b&ecirc;n cạnh những địa chỉ quen thuộc vốn l&agrave; s&acirc;n chơi H&egrave; &ldquo;truyền thống&rdquo; d&agrave;nh cho c&aacute;c em học sinh như Cung Thiếu nhi, trường thể thao thanh thiếu ni&ecirc;n 10/10, nh&agrave; văn ho&aacute; c&aacute;c quận, huyện&hellip; th&igrave; một &ldquo;điểm đến&rdquo; cũng đang được nhiều phụ huynh gửi niềm tin l&agrave; Trường Phố th&ocirc;ng Song ngữ Li&ecirc;n cấp Wellspring, nơi c&oacute; c&aacute;c trại h&egrave; dự &aacute;n với những hoạt động v&ocirc; c&ugrave;ng hấp dẫn v&agrave; th&uacute; vị.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">Dịp h&egrave; 2016 n&agrave;y, Trường Phố th&ocirc;ng Song ngữ Li&ecirc;n cấp Wellspring tiếp tục mang đến c&aacute;c Chương tr&igrave;nh h&egrave; d&agrave;nh cho học sinh trong v&agrave; ngo&agrave;i trường với nhiều trại h&egrave; dự &aacute;n v&agrave; giờ trải nghiệm được thiết kế đa dạng, hấp dẫn. Chương tr&igrave;nh được tổ chức trong 5 tuần từ ng&agrave;y 13/6 đến ng&agrave;y 15/7/2016.</span></p>\r\n\r\n<p><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=410&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/410/05-07-58-13-05-2016-vui-hoc-he-cung-nhung-trai-nghiem-cua-dam-me.jpg\" /></p>\r\n\r\n<p><strong><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">CHƯƠNG TR&Igrave;NH H&Egrave; TRUNG HỌC:&nbsp;LEARN MY WAY</span></strong></p>\r\n\r\n<ul>\r\n	<li>File th&ocirc;ng tin:&nbsp;<a href=\"http://wellspring.edu.vn/wp-content/uploads/2016/05/H%C3%A8-trung-h%E1%BB%8Dc-Learn-my-way-2016.pdf\" rel=\"\" style=\"box-sizing: border-box; border: 0px; font-family: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-decoration: none; color: rgb(0, 101, 135); line-height: inherit;\">Chương tr&igrave;nh H&egrave; trung học &ndash; LEARN MY WAY 2016</a></li>\r\n	<li>Form đăng k&yacute;:&nbsp;<a href=\"http://wellspring.edu.vn/wp-content/uploads/2016/05/phieu-dang-ky-he-Trung-hoc_-Learn-My-Way_2016.docx\" rel=\"\" style=\"box-sizing: border-box; border: 0px; font-family: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-decoration: none; color: rgb(0, 101, 135); line-height: inherit;\">Phiếu đăng k&yacute; h&egrave; Trung học &ndash; LEARN MY WAY 2016</a></li>\r\n</ul>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">Trong đời học sinh, l&uacute;c vui sướng nhất đ&oacute; l&agrave; khi nhắc đến m&ugrave;a h&egrave;, đến k&igrave; nghỉ d&agrave;i thỏa sức l&ecirc;n n&uacute;i, xuống biển m&agrave; kh&ocirc;ng c&oacute; sự hiện diện của s&aacute;ch vở, kh&ocirc;ng thầy c&ocirc;, kh&ocirc;ng b&agrave;i tập, kh&ocirc;ng thi cử.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">V&agrave; v&igrave; thế m&agrave; khi nhắc đến học h&egrave; &ndash; cụm từ n&agrave;y sẽ l&agrave; nỗi &aacute;m ảnh đối với mỗi học sinh, khi n&oacute; gợi nhớ tới: Lịch học th&ecirc;m d&agrave;y đặc với c&aacute;c m&ocirc;n To&aacute;n. Văn, L&yacute;, H&oacute;a&hellip;trong c&aacute;i n&oacute;ng hầm hập của m&ugrave;a h&egrave;.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">Đến lớp học h&egrave; chỉ để &ocirc;n lại c&aacute;c kiến thức cũ đ&atilde; học trong năm hoặc l&agrave; &ldquo;học trước&rdquo; những kiến thức c&oacute; trong s&aacute;ch gi&aacute;o khoa. Tuy nhi&ecirc;n đến với Chương tr&igrave;nh h&egrave; LEARN MY WAY 2016 d&agrave;nh cho học sinh Trung học từ 11 đến 16 tuổi của Wellspring, c&aacute;c bạn học sinh sẽ được học h&egrave; theo c&aacute;ch của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\"><strong>5 tuần học với 125 giờ trải nghiệm</strong></span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12pt\">Tại kh&oacute;a h&egrave; n&agrave;y, học sinh &ldquo;học h&egrave;&rdquo; như tham gia &ldquo;bữa tiệc buffet&rdquo; với v&ocirc; số &ldquo;m&oacute;n ăn&rdquo; để lựa chọn, từ c&aacute;c m&ocirc;n học thể thao năng khiếu chuy&ecirc;n s&acirc;u, đầy say m&ecirc;, mạnh mẽ như Đội cổ vũ trường học School Cheerleading, b&oacute;ng đ&aacute;, b&oacute;ng rổ, bơi lội,&hellip; đến c&aacute;c dự &aacute;n học tập mới lạ m&agrave; hiệu quả như Dự &aacute;n nghệ thuật To&aacute;n học với những ứng dụng trong thực tế; &ldquo;Life Diary &ndash; by all my sense&rdquo; &ndash; Nhật k&iacute; cảm nhận cuộc sống, thực nghiệm Khoa học STEM . Đặc biệt, m&ocirc;n Tiếng Anh tổ chức theo c&aacute;c chủ đề v&ocirc; c&ugrave;ng th&uacute; vị, độc đ&aacute;o với tối thiểu 60% thời gian với gi&aacute;o vi&ecirc;n bản ngữ &ndash; H&igrave;nh thức học sinh động &ndash; Phương ph&aacute;p đa dạng.</span></p>\r\n</div>\r\n</div>\r\n</div>\r\n', '05-07-58-13-05-2016-vui-hoc-he-cung-nhung-trai-nghiem-cua-dam-me.jpg', '05-07-58-13-05-20