INSERT INTO `web_product` VALUES ('74', null, 'Thuốc bổ não. tăng cường trí nhớ - DHA & EPA Orihiro', '0', '0', '0', '2', '', '2', '<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">DHA giữ vai tr&ograve; cực kỳ quan trọng trong qu&aacute; tr&igrave;nh ph&aacute;t triển của thai nhi, sơ sinh v&agrave; cả ở tuổi gi&agrave;. H&agrave;m lượng DHA trong n&atilde;o tăng 300 dến 500% ở n&atilde;o trẻ trong 3 th&aacute;ng cuối thời kỳ thai ngh&eacute;n. Bổ sung DHA cho c&aacute;c b&agrave; mẹ đang mang thai đem lại lợi &iacute;ch cho sự ph&aacute;t triển của n&atilde;o thai nhi, tăng chỉ số IQ ph&aacute;</span><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">t triển n&atilde;o bộ n&atilde;o v&agrave; chức năng thị gi&aacute;c của thai nhi (mang thai c&aacute;c b&agrave; mẹ chuyển DHA trực tiếp đến thai nhi để hỗ trợ ph&aacute;t triển n&atilde;o nhanh ch&oacute;ng v&agrave; v&otilde;ng mạc. DHA được cung cấp cho trẻ sơ sinh qua sữa mẹ).</span></p>\r\n', '<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif; font-size:14px\">Thuốc bổ n&atilde;o. tăng cường tr&iacute; nhớ - DHA &amp; EPA Orihiro</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif; font-size:14px\">Sản xuất : Nhật</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif; font-size:14px\">Đ&ocirc;i đi&ecirc;̀u v&ecirc;̀ DHA</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif; font-size:14px\">T&aacute;c dụng: DHA giữ vai tr&ograve; cực kỳ quan trọng trong qu&aacute; tr&igrave;nh ph&aacute;t triển của thai nhi, sơ sinh v&agrave; cả ở tuổi gi&agrave;. H&agrave;m lượng DHA trong n&atilde;o tăng 300 dến 500% ở n&atilde;o trẻ trong 3 th&aacute;ng cuối thời kỳ thai ngh&eacute;n. Bổ sung DHA cho c&aacute;c b&agrave; mẹ đang mang thai đem lại lợi &iacute;ch cho sự ph&aacute;t triển của n&atilde;o thai nhi, tăng chỉ số IQ ph&aacute;</span><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35); font-family:helvetica,arial,sans-serif; font-size:14px\">t triển n&atilde;o bộ n&atilde;o v&agrave; chức năng thị gi&aacute;c của thai nhi (mang thai c&aacute;c b&agrave; mẹ chuyển DHA trực tiếp đến thai nhi để hỗ trợ ph&aacute;t triển n&atilde;o nhanh ch&oacute;ng v&agrave; v&otilde;ng mạc. DHA được cung cấp cho trẻ sơ sinh qua sữa mẹ).<br />\r\n. -N&atilde;o v&agrave; v&otilde;ng mạc mắt l&agrave; 2 cơ quan đ&ograve;i hỏi h&agrave;m lượng DHA cao nhất để duy tr&igrave; c&aacute;c hoạt động thần kinh (như khả năng học tập v&agrave; ph&aacute;t triển tr&iacute; tuệ)v&agrave; sư tinh tường, nhanh nhạy ở cả người trẻ v&agrave; người gi&agrave;. DHA l&agrave; cơ sở hạ tầng của m&ocirc; n&atilde;o v&agrave; v&otilde;ng mạc mắt. N&oacute; gi&uacute;p t&aacute;i tạo c&aacute;c chất dẫn truyền thần kinh như phosphatidylserine giữ vai tr&ograve; quan trọng trong chức năng của n&atilde;o. DHA v&agrave; EPA b&igrave;nh thường kh&ocirc;ng c&oacute; trong nguồn thực phẩm gi&agrave;u ALA (a-linoleic acid) như dầu canola v&agrave; quả hạch. C&aacute;c nh&agrave; khoa học khuyến c&aacute;o rằng trong thời kỳ mang thai v&agrave; cho con b&uacute; lượng DHA được cung cấp chỉ đ&aacute;p ứng được 20% nhu cầu DHA cho cả mẹ v&agrave; con (300mg/ng&agrave;y). Lượng DHA cung cấp cho trẻ nhỏ l&agrave; cực kỳ thấp l&agrave; một vấn đề đang đặc biệt được quan t&acirc;m (trung b&igrave;nh 19mg/ng&agrave;y cho trẻ 3 tuổi ở Bắc Mỹ). Nh&igrave;n chung,nhu cầu ng&agrave;y c&agrave;ng tăng của cộng đồng về c&aacute;c sản phẩm của c&aacute; v&agrave; c&aacute;c thực phẩm chức năng c&oacute; chứa DHA&nbsp;<br />\r\nThực phẩm chức năng gi&uacute;p bổ n&atilde;o orihiro của Nhật<br />\r\nThuốc bổ n&atilde;o dha của nhật hộp 90 vi&ecirc;n Orihiro DHA 525 l&agrave; sản phẩm tăng cường sức khỏe - tr&iacute; n&atilde;o của Nhật Bản, với c&ocirc;ng thức DHA chiếm tới 70% , bổ sung nguồn DHA cao cho đối tượng l&agrave; người lao động vất vả, thức đ&ecirc;m, l&agrave;m việc tr&iacute; &oacute;c, sức khỏe sa s&uacute;t, đầu &oacute;c mệt mỏi, ăn uống thiếu chất đặc biệt l&agrave; thức ăn từ c&aacute;c loại c&aacute;&hellip; vi&ecirc;n thuốc uống bổ n&atilde;o dha Orihiro của Nhật cũng l&agrave; sản phẩm thay thế th&iacute;ch hợp để cho những ai đ&atilde; từng sử dụng c&aacute;c loại sản phẩm DHA &amp; EPA kh&aacute;c với h&agrave;m lượng lớn, với gi&aacute; th&agrave;nh rẻ hơn v&agrave; với mục đ&iacute;ch duy tr&igrave; lượng DHA &amp; EPA cần thiết cho cơ thể khỏe mạnh.<br />\r\n<br />\r\nDHA l&agrave; g&igrave;<br />\r\nDHA l&agrave; acid b&eacute;o kh&ocirc;ng no cần thiết, c&oacute; t&ecirc;n gọi đầy đủ l&agrave; DocosaHexaenoicAacid m&agrave; cơ thể con người kh&ocirc;ng tự tổng hợp được.<br />\r\nDHA cần thiết cho ph&aacute;t triển ho&agrave;n thiện chức năng nh&igrave;n của mắt, sự ph&aacute;t triển ho&agrave;n hảo hệ thần kinh. Ở người trưởng th&agrave;nh, DHA c&oacute; t&aacute;c dụng giảm cholesterol to&agrave;n phần, triglyceride m&aacute;u, LDL-cholesterol (cholesterol xấu) g&acirc;y vữa xơ động mạnh - căn nguy&ecirc;n bệnh nhồi m&aacute;u cơ tim.<br />\r\nNếu thiếu DHA trong qu&aacute; tr&igrave;nh ph&aacute;t triển, trẻ sẽ c&oacute; chỉ số th&ocirc;ng minh IQ thấp. DHA c&oacute; nhiều trong dầu c&aacute;, c&aacute; v&agrave; thủy sản.<br />\r\n<br />\r\nC&ocirc;ng dụng của thuốc bổ n&atilde;o dha 525mg hộp 90 vi&ecirc;n<br />\r\nThuốc bổ n&atilde;o dha của nhật hộp 90 vi&ecirc;n rất hiệu quả trong việc bổ sung DHA &amp; EPA cho những người l&agrave;m việc tr&iacute; &oacute;c với cường độ mạnh, những người thức đ&ecirc;m, ăn uống thiếu chất, những người lao động vất vả. Thực phẩm chức năng gi&uacute;p bổ n&atilde;o orihiro của Nhật c&oacute; hiệu quả l&agrave;m tăng cường tr&iacute; nhớ cho học sinh trong học tập.Gi&uacute;p s&aacute;ng mắt,nh&igrave;n r&otilde; hơn nếu được cung cấp DHA đầy đủ<br />\r\nVi&ecirc;n uống bổ n&atilde;o DHA 525mg hộp 90 vi&ecirc;n c&ograve;n được d&ugrave;ng như một loại thuốc điều trị bệnh tiền đ&igrave;nh hiệu quả do c&oacute; chứa th&agrave;nh phần DHA h&agrave;m lượng cao, thuốc bổ n&atilde;o dha 525mg lọ 90 vi&ecirc;n đ&oacute;ng vai tr&ograve; như một loại thuốc chữa rối loạn tiền đ&igrave;nh dưỡng n&atilde;o. N&oacute; c&ograve;n được d&ugrave;ng l&agrave;m thuốc bổ n&atilde;o tăng tr&iacute; nhớ d&agrave;nh cho người gi&agrave; rất hiệu quả.<br />\r\n<br />\r\nHướng dẫn sử dụng:<br />\r\nTrẻ em ng&agrave;y d&ugrave;ng 1 vi&ecirc;n / ng&agrave;y<br />\r\nNgười lớn 3 vi&ecirc;n/ ng&agrave;y.</span></p>\r\n', '12-25-03-25-04-2016-12791042119990167003496697340983155302227n.jpg', '12-25-03-25-04-2016-12791042119990167003496697340983155302227n.jpg', 'a:1:{i:0;s:66:\"12-25-03-25-04-2016-12791042119990167003496697340983155302227n.jpg\";}', '0', '166', 'Chăm sóc cơ thể', '0', '0', '1', '1', '6', 'Hàng xách tay', '2', '22', '1461561792', null);
INSERT INTO `web_product` VALUES ('76', null, 'Sữa tươi Devondale Úc túi 1kg', '410000', '0', '410000', '1', '', '2', '<p><span style=\"background-color:rgb(255, 255, 255)\">Sữa tươi dạng bột Devondale (nguy&ecirc;n kem) (1kg) sản xuất tại &Uacute;c v&ocirc; c&ugrave;ng nổi tiếng tr&ecirc;n thế giới bởi h&agrave;m lượng canxi v&agrave; c&aacute;c chất dinh dưỡng rất cao gi&uacute;p b&eacute; l&ecirc;n c&acirc;n v&agrave; ph&aacute;t triển chiều cao c&acirc;n đối. Đặc biệt, sữa thơm ngon v&agrave; b&eacute;o ngậy, c&oacute; thể cho b&eacute; uống theo nhu cầu. D&agrave;nh cho b&eacute; từ 2&nbsp;tuổi trở l&ecirc;n.</span></p>\r\n', '<h1>Sữa tươi dạng bột Devondale (nguy&ecirc;n kem) (1kg)</h1>\r\n\r\n<ul>\r\n	<li>Sữa tươi dạng bột Devondale l&agrave; h&atilde;ng sữa tươi nổi tiếng số 1 của &uacute;c ,được xuất sang c&aacute;c nước tr&ecirc;n thế giới với h&agrave;m lượng canxi rất cao, gi&uacute;p cung cấp canxi,tăng chiều cao.</li>\r\n	<li>1kg pha được 7 l&iacute;t sữa tươi nước</li>\r\n	<li>đặc điểm của loại sữa tươi dạng bột n&agrave;y kg c&oacute; chất bảo quản như những loại sữa tươi nước th&ocirc;ng thường n&ecirc;n ho&agrave;n to&agrave;n an to&agrave;n cho con y&ecirc;u của ch&uacute;ng ta sử dụng, giữa l&uacute;c thị trường sữa đang biến động v&igrave; nhiều sữa nhiễm khuẩn v&agrave; độc hại th&igrave; đ&acirc;y l&agrave; loại sữa c&aacute;c mẹ c&oacute; thể y&ecirc;n t&acirc;m chọn lựa cho con m&igrave;nh.&nbsp;</li>\r\n	<li>Tất cả c&aacute;c sản phẩm sữa của Devondale đều l&agrave; sữa tươi nguy&ecirc;n chất, kh&ocirc;ng c&oacute; đường, kh&ocirc;ng sử dụng chất bảo quản, kh&ocirc;ng hương liệu, kh&ocirc;ng chất phụ gia như&nbsp;những loại sữa tươi nước th&ocirc;ng thường n&ecirc;n rất tốt cho sức khỏe, đặc biệt l&agrave; sức khỏe v&agrave; sự ph&aacute;t triển về chiều cao của trẻ.</li>\r\n</ul>\r\n\r\n<p><strong>C&aacute;ch d&ugrave;ng:</strong><br />\r\n-&gt; Để pha 1 ly sữa 250ml th&igrave; lấy 1/3 ly (35gr sữa bột) cho v&agrave;o ly sau đ&oacute; cho 1 nửa ly nước n&oacute;ng hoặc lạnh t&ugrave;y th&iacute;ch v&agrave;o quậy đều cho đến khi tan hết bột sữa rồi đổ nốt 1 nửa ly nước c&ograve;n lại v&agrave;o..</p>\r\n\r\n<p>-&gt; Để pha 1 l&iacute;t sữa th&igrave; lấy 1+1/3 ly (140gr sữa bột) cho v&agrave;o b&igrave;nh sau đ&oacute; cho 500ml nước n&oacute;ng hoặc lạnh tuỳ th&iacute;ch v&agrave;o quậy đều cho đến khi tan hết bột sữa th&igrave; đổ nốt 500ml nước c&ograve;n lại v&agrave;o...</p>\r\n\r\n<p>-&gt; Bảo quản ở nơi kh&ocirc; tho&aacute;ng<br />\r\n-&gt; Sữa đ&atilde; pha nước xin giữ ở tủ lạnh 4 độ C v&agrave; sử dụng kg qu&aacute; 3 ng&agrave;y từ l&uacute;c pha..</p>\r\n\r\n<ul>\r\n	<li>Ngo&agrave;i c&ocirc;ng dụng pha th&agrave;nh sữa tươi bạn c&oacute; d&ugrave;ng để l&agrave;m b&aacute;nh nữa nh&eacute;..</li>\r\n	<li>Sữa kh&ocirc;ng chất bảo quản.&nbsp;</li>\r\n	<li>Sản phẩm NK &amp; sx tại &Uacute;c.&nbsp;</li>\r\n</ul>\r\n', '12-28-52-25-04-2016-1282142111999049733679691381425398110916765n.jpg', '12-28-52-25-04-2016-1282142111999049733679691381425398110916765n.jpg', 'a:1:{i:0;s:68:\"12-28-52-25-04-2016-1282142111999049733679691381425398110916765n.jpg\";}', '0', '166', 'Chăm sóc cơ thể', '0', '0', '1', '1', '6', 'Hàng xách tay', '2', '22', '1461562132', '1461567262');
